  `ip` varchar(100) NOT NULL DEFAULT '' COMMENT '最后一次登录ip',
  `qq` varchar(100) NOT NULL DEFAULT '' COMMENT 'qq',
  `wechat` varchar(100) NOT NULL DEFAULT '' COMMENT '微信',
  `address` varchar(100) NOT NULL DEFAULT '' COMMENT '现居地',
  `work` varchar(100) NOT NULL DEFAULT '' COMMENT '实际工作',
